<?php/** * Plugin Activation *//** * Activation Hook */register_activation_hook( CCM_FILE, 'ccm_activation' );function ccm_activation() {	// Flush rewrite rules so friendly URL's for custom post types and taxonomies take effect	add_action( 'init', 'ccm_flush_rewrite_rules', 11 ); // after post types, taxonomies registered}/** * Flush Rewrite Rules */function ccm_flush_rewrite_rules() {	flush_rewrite_rules();	}