<?php/** * Localization */// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.// NOTE: make it easy (even default) to setup languages OUTSIDE of plugin, to protect in updates.add_action( 'plugins_loaded', 'ccm_load_textdomain' );function ccm_load_textdomain() {	// Path to languages directory	$languages_dir = CCM_DIR . '/languages/';	$languages_dir = apply_filters( 'ctc_languages_dir', $languages_dir ); // make filterale	// Ensure trailing slash	$languages_dir = trailingslashit( $languages_dir );		// Load textdomain	load_plugin_textdomain( 'ccm', false, $languages_dir );}